package com.rappytv.opsuchtaddon;

import com.rappytv.opsuchtaddon.gui.icondata.DynamicIconData;
import com.rappytv.opsuchtaddon.gui.modules.*;
import com.rappytv.opsuchtaddon.listener.forge.ClientDisconnectionFromServerEvent;
import com.rappytv.opsuchtaddon.listener.forge.ClientConnectedToServerListener;
import com.rappytv.opsuchtaddon.listener.labymod.MessageModifyListener;
import com.rappytv.opsuchtaddon.listener.labymod.ServerMessageListener;
import com.rappytv.opsuchtaddon.server.OPSuchtLabyServer;
import com.rappytv.opsuchtaddon.settings.AddonSettings;
import lombok.Getter;
import net.labymod.api.LabyModAddon;
import net.labymod.ingamegui.ModuleCategory;
import net.labymod.ingamegui.ModuleCategoryRegistry;
import net.labymod.ingamegui.moduletypes.SimpleModule;
import net.labymod.settings.elements.BooleanElement;
import net.labymod.settings.elements.ControlElement.IconData;
import net.labymod.settings.elements.HeaderElement;
import net.labymod.settings.elements.SettingsElement;
import net.labymod.utils.Material;
import net.labymod.utils.ModColor;

import java.util.Arrays;
import java.util.LinkedList;
import java.util.List;
import java.util.logging.Logger;
import java.util.stream.Collectors;

public class OPSuchtAddon extends LabyModAddon {

    @Getter
    private static OPSuchtAddon instance;

    @Getter
    private AddonSettings addonSettings;

    @Getter
    private OPSuchtLabyServer opSuchtLabyServer;

    @Getter
    private final Logger logger = Logger.getLogger("Minecraft");

    @Getter
    private final List<LabyModule> modules = new LinkedList<>();

    @Getter
    private final ModuleCategory opSuchtModuleCategory = new ModuleCategory("OPSUCHT", true,
            new DynamicIconData("opsucht_logo",
                    "data:image/png;base64,"));

    @Override
    public void onEnable() {
        instance = this;

        // ---

        ModuleCategoryRegistry.loadCategory(this.opSuchtModuleCategory);

        this.loadModules();

        this.opSuchtLabyServer = new OPSuchtLabyServer("opsucht_network",
                "opsucht.net", "play.opsucht.net", "mc.opsucht.net");

        this.getApi().registerServerSupport(this, this.getOpSuchtLabyServer());


        getApi().registerForgeListener(new ClientDisconnectionFromServerEvent(this));
        getApi().registerForgeListener(new ClientConnectedToServerListener(this));
        getApi().getEventManager().register(new ServerMessageListener());
        getApi().getEventManager().register(new MessageModifyListener(this));
    }

    @Override
    public void loadConfig() {
        this.addonSettings = new AddonSettings(
                !this.getConfig().has("clickableNicks") || this.getConfig().get("clickableNicks").getAsBoolean()
        );
    }

    @Override
    protected void fillSettings(List<SettingsElement> settingsElements) {
        settingsElements.add(new HeaderElement(ModColor.YELLOW + "Klickbare Nicknamen"));
        settingsElements.add(
                new BooleanElement(ModColor.GOLD + "Aktiviert", new IconData(Material.LEVER), value -> {
                    this.addonSettings.setClickableNicks(value);
                    this.getConfig().addProperty("clickableNicks", value);
                    this.saveConfig();
                }, this.addonSettings.getClickableNicks())
        );
    }

    private void loadModules() {
        this.getModules().addAll(Arrays.asList(
                new VanishModule(), new FlyModule(), new GodModule(),
                new GlowModule()
        ));

        this.getModules().forEach(this.getApi()::registerModule);

        this.getLogger().info(String.format("Registered module(s): %s", this.getModules().stream().map(SimpleModule::getDisplayName).collect(Collectors.joining(", "))));
    }
}